import React, { useEffect, useState } from 'react';
import { Table, Form, Button } from 'react-bootstrap';
import { getProducts } from '../services/ProduitsServices';
import "../App.css";

const Products = () => {
  const [products, setProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    let mounted = true;
    getProducts()
      .then(data => {
        if (mounted) {
          setProducts(data);
        }
      })
      .catch(error => {
        console.error('Erreur lors du chargement des produits', error);
      });

    return () => mounted = false;
  }, []);

  const handleSearch = () => {
    // Faites une copie de la liste complète de produits pour ne pas modifier les données d'origine
    const filteredProducts = [...products];

    // Utilisez la méthode filter() pour filtrer les produits en fonction du nom de recherche
    const filteredResults = filteredProducts.filter(product =>
      product.nom.toLowerCase().includes(searchQuery.toLowerCase())
    );

    // Mettez à jour la liste des produits avec les résultats filtrés
    setProducts(filteredResults);
  };

  const handleReset = () => {
    // Réinitialisez la liste des produits avec la liste complète non filtrée
    getProducts()
      .then(data => {
        setProducts(data);
        setSearchQuery('');
      })
      .catch(error => {
        console.error('Erreur lors du chargement des produits', error);
      });
  };

  return (
    <div className="row side-row">
      <Form>
        <Form.Group controlId="searchQuery">
          <Form.Control
            type="text"
            placeholder="Rechercher un produit..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" onClick={handleSearch}>
          Rechercher
        </Button>
        <Button variant="danger" onClick={handleReset}>
          Réinitialiser
        </Button>
      </Form>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nom</th>
            <th>Prix Unitaire</th>
            <th>Quantité</th>
          </tr>
        </thead>
        <tbody>
          {products.map(pro =>
            <tr key={pro.id + "-" + pro.nom}>
              <td>{pro.id}</td>
              <td>{pro.nom}</td>
              <td>{pro.prix_unitaire}</td>
              <td>{pro.quantite}</td>
            </tr>
          )}
        </tbody>
      </Table>
    </div>
  );
};

export default Products;
