import react,{ useEffect,useState} from 'react';
import {Table} from 'react-bootstrap';
import { ButtonToolbar } from 'react-bootstrap';
import  "../App.css";
import {Button, ButtonToolBar} from 'react-bootstrap';
import{getProducts, deleteProduct} from '../services/ProduitsServices';
import AddProductModal from './AddProductModal';
import UpdateProductModal from './UpdateProductModal';

const Manage = () =>{
    const [products, setProducts]=useState([]);
    const [addModalShow, setAddModalShow]=useState(false);
    const [editModalShow, setEditModalShow]=useState(false);
    const [editProduct, setEditProduct]=useState([]);
    const [isUpdated, setIsUpdated] = useState(false);
    useEffect(() => {
        let mounted = true;
        if (products.length && !isUpdated){
            return;
        }
        getProducts()
            .then(data =>{
            if (mounted){
            setProducts(data)
            }
            })
        return () => {
            mounted = false
            setIsUpdated(false);
           };

    }, [isUpdated, products]);

    const handleAdd = (e) => {
        e.preventDefault();
        setAddModalShow(true);
    };

     const handleUpdate = (e, pro) => {
        e.preventDefault();
        setEditModalShow(true);
        setEditProduct(pro);
    };

    const handleDelete = (e, id) => {
        if(window.confirm('Etes vous sûre ?')){
            e.preventDefault();
            deleteProduct(id)
            .then((result)=>{
                alert(result);
                setIsUpdated(true);
            },
            (error)=>{
                alert("impossible de supprimer le produit");
           })
            }



    };
    let AddModalClose = () =>setAddModalShow(false);
    let EditModalClose = () =>setEditModalShow(false);
    return (
    <div className="row side-row">
     <Table striped bordered hover>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nom</th>
              <th>Prix Unitaire</th>
              <th>Quantité</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {products.map((pro) =>
            <tr key={pro.id + "-" + pro.nom}>
              <td>{pro.id}</td>
              <td>{pro.nom}</td>
              <td>{pro.prix_unitaire}</td>
              <td>{pro.quantite}</td>
               <td>
                <Button className='mr-2' variant="warning"
                onClick={event => handleUpdate(event,pro)}>Mise à Jour</Button>{' '}
                <UpdateProductModal show={editModalShow} onHide ={EditModalClose}
                 product={editProduct} setUpdated={setIsUpdated}
                setUpdated={setIsUpdated}>

                </UpdateProductModal>
                <Button className='mr-2' variant="danger"
                onClick={event => handleDelete(event,pro.id)}>Supprimer</Button>{' '}

               </td>

            </tr>)}


          </tbody>
        </Table>
        <ButtonToolbar>
         <Button variant="primary" onClick={handleAdd}>Ajouter Produit</Button>{' '}
         <AddProductModal show={addModalShow} onHide ={AddModalClose} setUpdated={setIsUpdated}>
         </AddProductModal>
        </ButtonToolbar>
        </div>
      );
};
export default Manage;
