import React, { useEffect, useState } from 'react';
import { Table, Form, Button, InputGroup } from 'react-bootstrap';
import { getProducts } from '../services/ProduitsServices';
import { FaSearch } from 'react-icons/fa';
import "../App.css";

const Products = () => {
  // ... (code précédent)

  return (
    <div className="row side-row">
      <Form>
        <Form.Group controlId="searchQuery" className="d-flex align-items-center">
          <InputGroup>
            <InputGroup.Prepend>
              <InputGroup.Text>
                <FaSearch />
              </InputGroup.Text>
            </InputGroup.Prepend>
            <Form.Control
              type="text"
              placeholder="Rechercher un produit..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </InputGroup>
          <Button variant="primary" onClick={handleSearch}>
            Rechercher
          </Button>
        </Form.Group>
      </Form>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nom</th>
            <th>Prix Unitaire</th>
            <th>Quantité</th>
          </tr>
        </thead>
        <tbody>
          {products.map((pro) => (
            <tr key={pro.id + "-" + pro.nom}>
              <td>{pro.id}</td>
              <td>{pro.nom}</td>
              <td>{pro.prix_unitaire}</td>
              <td>{pro.quantite}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default Products;
