import React, { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';
import { getProducts } from '../services/ProduitsServices';
import { FaSearch } from 'react-icons/fa';
import "./Products.css"; // Import the CSS file here
import "../App.css";


const Products = () => {
  const [products, setProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    let mounted = true;
    getProducts()
      .then(data => {
        if (mounted) {
          setProducts(data);
        }
      })
      .catch(error => {
        console.error('Erreur lors du chargement des produits', error);
      });

    return () => (mounted = false);
  }, []);

  const handleSearch = () => {
    if (searchQuery.trim() === '') {
      // Si la recherche est vide, on réinitialise les produits
      getProducts()
        .then(data => {
          setProducts(data);
        })
        .catch(error => {
          console.error('Erreur lors du chargement des produits', error);
        });
    } else {
      const filteredProducts = [...products];

      // La fonction normalize() permet de transformer le texte en utilisant la "normalisation" Unicode
      // Cela nous permet de rendre la recherche insensible aux accents et à la casse
      const normalizedSearchQuery = searchQuery
        .toLowerCase()
        .normalize('NFD')
        .replace(/[\u0300-\u036f]/g, '');

      const filteredResults = filteredProducts.filter(
        (product) =>
          product.nom
            .toLowerCase()
            .normalize('NFD')
            .replace(/[\u0300-\u036f]/g, '')
            .includes(normalizedSearchQuery)
      );

      setProducts(filteredResults);
    }
  };

  return (
    <div className="row side-row">
      <Form>
        <Form.Group controlId="searchQuery">
          <Form.Control
            type="text"
            placeholder="Rechercher un produit..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" onClick={handleSearch}>
          <FaSearch />
        </Button>
      </Form>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nom</th>
            <th>Prix Unitaire</th>
            <th>Quantité</th>
          </tr>
        </thead>
        <tbody>
          {products.map((pro) => (
            <tr key={pro.id + "-" + pro.nom}>
              <td>{pro.id}</td>
              <td>{pro.nom}</td>
              <td>{pro.prix_unitaire}</td>
              <td>{pro.quantite}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default Products;
