import { Table, Form, Button, InputGroup } from 'react-bootstrap';
import { getProducts } from '../services/ProduitsServices';
import { FaSearch } from 'react-icons/fa';
import "../App.css";

const Products = () => {
  const [products, setProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState(''); // Add this line for search query state

  // ... (code for useEffect and other parts)

  const handleSearch = () => {
    // ... (code for handleSearch function)
  };

  return (
    <div className="row side-row">
      <Form>
        <Form.Group controlId="searchQuery" className="d-flex align-items-center">
          <InputGroup>
            <InputGroup.Prepend>
              <InputGroup.Text>
                <FaSearch />
              </InputGroup.Text>
            </InputGroup.Prepend>
            <Form.Control
              type="text"
              placeholder="Rechercher un produit..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </InputGroup>
          <Button variant="primary" onClick={handleSearch}>
            Rechercher
          </Button>
        </Form.Group>
      </Form>
      <Table striped bordered hover>
        {/* ... (code for the table) */}
      </Table>
    </div>
  );
};

export default Products;
