import react,{ useEffect,useState} from 'react';
import {Table} from 'react-bootstrap';
import { ButtonToolbar } from 'react-bootstrap';
import  "../App.css";
import {Button, ButtonToolBar} from 'react-bootstrap';
import{getProducts, deleteProduct} from '../services/ProduitsServices';
import AddProductModal from './AddProductModal';
import UpdateProductModal from './UpdateProductModal';
import { FaEdit } from 'react-icons/fa';
import { RiDeleteBin5Line } from 'react-icons/ri';
import axios from 'axios';
import { FaSearch } from 'react-icons/fa';
import { Form, InputGroup } from 'react-bootstrap';
const Manage = () =>{
    const [products, setProducts]=useState([]);
    const [addModalShow, setAddModalShow]=useState(false);
    const [editModalShow, setEditModalShow]=useState(false);
    const [editProduct, setEditProduct]=useState([]);
    const [isUpdated, setIsUpdated] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');
    useEffect(() => {
        let mounted = true;
        if (products.length && !isUpdated){
            return;
        }
        getProducts()
            .then(data =>{
            if (mounted){
            setProducts(data)
            }
            })
        return () => {
            mounted = false
            setIsUpdated(false);
           };

    }, [isUpdated, products]);

    const handleAdd = (e) => {
        e.preventDefault();
        setAddModalShow(true);
    };

     const handleUpdate = (e, pro) => {
        e.preventDefault();
        setEditModalShow(true);
        setEditProduct(pro);
    };

    const handleDelete = (e, id) => {
        if(window.confirm('Etes vous sûre ?')){
            e.preventDefault();
            deleteProduct(id)
            .then((result)=>{
                alert(result);
                setIsUpdated(true);
            },
             (error)=>{
                alert("impossible de supprimer le produit");
           })

         }
    };

     const handleSearch = (e) => {
    e.preventDefault();
    // Appeler votre API de recherche en fonction de la valeur searchQuery
    // Par exemple, utilisez axios pour effectuer une requête GET à votre endpoint de recherche.
    axios.get('http://127.0.0.1:8000/produits/', { params: { query: searchQuery } })
      .then((response) => {
        setProducts(response.data); // Mettre à jour les produits avec les résultats filtrés
      })
      .catch((error) => {
        console.error('Erreur lors de la recherche de produits', error);
      });
  };
  

    let AddModalClose = () =>setAddModalShow(false);
    let EditModalClose = () =>setEditModalShow(false);
    return (
    <div className="row side-row">
     <Table striped bordered hover>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nom</th>
              <th>Prix Unitaire</th>
              <th>Quantité</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {products.map((pro) =>
            <tr key={pro.id + "-" + pro.nom}>
              <td>{pro.id}</td>
              <td>{pro.nom}</td>
              <td>{pro.prix_unitaire}</td>
              <td>{pro.quantite}</td>
               <td>
               <Button className='mr-2' variant="danger"
                onClick={event => handleDelete(event,pro.id)}>
                 <RiDeleteBin5Line />
                </Button>
                 <span>&nbsp;&nbsp;</span>
                <Button className='mr-2' variant="warning"
                onClick={event => handleUpdate(event,pro)}>
                      <FaEdit />
                </Button>

                <UpdateProductModal show={editModalShow} onHide ={EditModalClose}
                 product={editProduct} setUpdated={setIsUpdated}
                setUpdated={setIsUpdated}>
                </UpdateProductModal>


               </td>

            </tr>)}


          </tbody>
        </Table>
        <ButtonToolbar>
         <Button variant="primary" onClick={handleAdd}>Ajouter Produit</Button>{' '}
         <AddProductModal show={addModalShow} onHide ={AddModalClose} setUpdated={setIsUpdated}>
         </AddProductModal>
        </ButtonToolbar>
        </div>
      );
};
export default Manage;
